import React from 'react';
import './Facts.scss';
import SlideFacts from './SlideFacts';
//import FactsItem from './FactsItem';
import SwiperCore, {Navigation, Pagination} from 'swiper';
import {Swiper, SwiperSlide} from 'swiper/react';
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';

SwiperCore.use([Navigation, Pagination])

const Facts = ({title, slides}) => {
    const factsSlides= slides.map( (slide) => 
    <SwiperSlide> 
    <SlideFacts item={slide} key={slide.id}/>
    </SwiperSlide> );
    
    
    return (
        <section className="facts">
            <h2 className="facts-title">{title}</h2>
            <div className="swiper-buttom-prev"/>
            <Swiper
                navigation={{
                    nextEl:'swiper-button-next',
                    prevEl:'swiper-button-prev'
                }}

                pagination={{
                    el:'facts-pagination',
                    clickable:true,
                    renderBullet: function (index, className) {
						return `<span class="facts-pagination-bullet ${className}"></span>`;}
                }}
                
                //initialSlide={0}
                
               //spaceBetween={30}
				slidesPerView={3}
				slidesPerGroup={3}
                centeredSlides={false}
                //centeredSlidesBounds={true}
                
            >

                {factsSlides}
            </Swiper>



            <div className="swiper-buttom-next"/>
			<div className="facts-pagination"/>

        </section>


    )
}
export default Facts;
