import React from 'react';
import './App.scss';
//импорт компонентов
import HeroBlock from './components/HeroBlock';
//импорт переменных
import gcLogo from './assets/GC_logo.svg';
import ITNLogo from './assets/ITN_logo.svg';
import shidLogo from './assets/shid_emozhl_logo.svg';
import heroBG from './assets/hero-bg.jpg';


function App() {
  //массив логотипов
const partnersLogo = [shidLogo, ITNLogo, gcLogo];
  //массив отв. за ссылки
const navLinks = [
  { title: "Основные факты",
    href: "#facts"
  },
  { title: "Индекс качества воздуха",
    href: "#quality"
  },
  'Основные факты','Индекс качества воздуха',];
const heroBG = [];


  return (

    <HeroBlock title='Качество атмосферного воздуха и здоровье' 
                logos={partnersLogo}
                bgImg={heroBG}
                links={navLinks}/>


  );
}

export default App;
