import React from 'react';
import './HeroBlock.scss';

const HeroBlock = ({title, links, logos}) => {
    /*создана переменная=новый массив уже преобразованный из логос в имж для рендера*/
    const logosImages = logos.map((logoItem) => <img src={logoItem} alt="partners-Logo" className="hero-partners-item"/>);
    //
    const navItems = logos.map((link) => <a href={link.href} className="hero-navigation-item">{link.title} </a>);
    const bgcss = {background: 'url(${bgImg})'};

return (
    //контейнер на всю ширину
    <section className="hero">
        <div className="container">
            <div className="container">
                <div className="hero-partners">
                    {logosImages}
                      Array of p logos 
                </div> 
                <nav  className="hero-nav">
                    {navItems}
                Array of links
                </nav>    
                <h1 className="hero-title">{title}</h1> 
                <a href="#" className="hero-link">Отследить</a>
            </div>
        </div>
    </section>
);
};

export default HeroBlock;
